// export default ChatApp;

const express = require("express");
const http = require("http");
const socketIO = require("socket.io");

const app = express();
const server = http.createServer(app);
const io = socketIO(server, {
    cors: {
        origin: "http://localhost:3000",
        methods: ["GET", "POST"],
    },
});

// Handle Socket.IO connections
io.on("connection", (socket) => {
    console.log(`âš¡: User connected with ID: ${socket.id}`);

    // Join a room
    socket.on("joinRoom", (roomID) => {
        socket.join(roomID);
        console.log(`User with ID ${socket.id} joined room: ${roomID}`);
        socket.to(roomID).emit("message", `User ${socket.id} joined the room.`);
    });

    // Receive and broadcast messages within a specific room
    socket.on("sendMessage", ({ roomID, message }) => {
        console.log(`Message in room ${roomID}:`, message);
        socket.to(roomID).emit("receiveMessage", message); // Broadcast to others in the room
    });

    // Handle disconnection
    socket.on("disconnect", () => {
        console.log(`ðŸ”¥: User disconnected with ID: ${socket.id}`);
    });
});

// Start the server
const PORT = 4000;
server.listen(PORT, () => {
    console.log(`Server is running on http://localhost:${PORT}`);
});
