import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

// Initialize Socket.IO client
const socket = io("http://192.168.101.32:4000");

const ChatApp = () => {
  const [username, setUsername] = useState(""); // User's name
  const [email, setEmail] = useState(""); // User's email
  const [roomID, setRoomID] = useState(""); // Room ID
  const [message, setMessage] = useState(""); // Message input
  const [messages, setMessages] = useState([]); // Chat history
  const [isInRoom, setIsInRoom] = useState(false); // Track if user is in a room
  const [createdRoomID, setCreatedRoomID] = useState(""); // Store generated room ID
  const [isRegistered, setIsRegistered] = useState(false); // Registration status
  const [isJoiningRoom, setIsJoiningRoom] = useState(false); // Track if user is joining a room
  const [users, setUsers] = useState([]); // Track list of users in the room
  const [selectedUser, setSelectedUser] = useState(null); // Track selected user for direct messages

  // Listen for incoming events from the backend
  useEffect(() => {
    socket.on("receiveMessage", (newMessage) => {
      if (newMessage.sender !== username) {
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      }
    });

    socket.on("message", (systemMessage) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "System", text: systemMessage },
      ]);
    });

    socket.on("roomCreated", ({ roomID, username }) => {
      setCreatedRoomID(roomID);
      setRoomID(roomID);
      setIsInRoom(true);
    });

    socket.on("roomJoined", ({ roomID, username, users }) => {
      setRoomID(roomID);
      setUsers(users);
      setIsInRoom(true);
    });

    socket.on("userJoined", (newUser) => {
      setUsers((prevUsers) => [...prevUsers, newUser]);
    });

    socket.on("userLeft", (userToRemove) => {
      setUsers((prevUsers) => prevUsers.filter(user => user !== userToRemove));
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("message");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("userJoined");
      socket.off("userLeft");
    };
  }, [username]);

  const handleRegistration = () => {
    if (username.trim() && email.trim()) {
      setIsRegistered(true);
    } else {
      alert("Please enter both name and email!");
    }
  };

  const createRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter your name first.");
    }
  };

  const joinRoom = () => {
    if (username.trim() && roomID.trim()) {
      socket.emit("joinRoom", { roomID, username });
    } else {
      alert("Please enter your name and room ID.");
    }
  };

  const sendMessage = () => {
    if (message.trim() && roomID.trim()) {
      socket.emit("sendMessage", { roomID, username, message, receiver: selectedUser });
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "You", text: message },
      ]);
      setMessage("");
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  const handleUserClick = (user) => {
    setSelectedUser(user);
    setMessages([]); // Clear previous messages on selecting a user
  };

  return (
    <div className="chat-container">
      {!isRegistered ? (
        <div className="registration-container">
          <h2>Enter Your Details</h2>
          <input
            type="text"
            placeholder="Your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleRegistration}>Submit</button>
        </div>
      ) : !isInRoom ? (
        <div className="room-container">
          <h2>Enter Your Room</h2>
          <input
            type="text"
            placeholder="Enter room name"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
            disabled={isJoiningRoom}
          />
          <div className="room-buttons">
            <button
              onClick={createRoom}
              disabled={isJoiningRoom}
              className="room-button"
            >
              Create Room
            </button>
            <button
              onClick={() => setIsJoiningRoom(true)}
              disabled={createdRoomID}
              className="room-button"
            >
              Join Room
            </button>
          </div>

          {isJoiningRoom && (
            <div>
              <input
                type="text"
                placeholder="Room ID"
                value={roomID}
                onChange={(e) => setRoomID(e.target.value)}
              />
              <button onClick={joinRoom}>Join Room</button>
            </div>
          )}
        </div>
      ) : (
        <>
          <div className="chat-header">
            <h2>Room: {roomID}</h2>
            <p>Logged in as: {username}</p>
          </div>

          <div className="chat-layout">
            <div className="user-list">
              <h3>Users:</h3>
              {users.map((user, index) => (
                <div
                  key={index}
                  className="user-item"
                  onClick={() => handleUserClick(user)}
                >
                  {user}
                </div>
              ))}
            </div>

            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div
                  key={index}
                  className={msg.sender === "You" ? "message-sender" : "message-receiver"}
                >
                  <strong>{msg.sender}:</strong> {msg.text}
                </div>
              ))}
            </div>
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyPress}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatApp;
