import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate, useParams } from "react-router-dom";
import { io } from "socket.io-client";
import "./App.css"; // Include your CSS styles here

const socket = io("http://192.168.101.32:4000"); // Replace with your backend URL

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainComponent />} />
        <Route path="/chat/:roomID" element={<ChatRoom />} />
      </Routes>
    </Router>
  );
};

const MainComponent = () => {
  const [step, setStep] = useState(1); // Step 1: Register, Step 2: Create/Join Room
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [roomName, setRoomName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [createdRoomID, setCreatedRoomID] = useState(null);
  const navigate = useNavigate();

  // Handle User Registration
  const handleRegister = () => {
    if (name.trim() && email.trim()) {
      setStep(2); // Move to room creation/join step
    } else {
      alert("Please enter a valid name and email!");
    }
  };

  // Handle Room Creation
  const handleCreateRoom = () => {
    if (roomName.trim()) {
      socket.emit("createRoom", { name: roomName }, (response) => {
        if (response.success) {
          setCreatedRoomID(response.roomID);
          alert(`Room "${roomName}" created with ID: ${response.roomID}`);
        } else {
          alert("Error creating room. Try again.");
        }
      });
    } else {
      alert("Please enter a room name!");
    }
  };

  // Handle Joining a Room
  const handleJoinRoom = () => {
    if (roomID.trim()) {
      navigate(`/chat/${roomID}`);
    } else {
      alert("Please enter a valid room ID!");
    }
  };

  return (
    <div className="main-container">
      {step === 1 && (
        <div className="registration-container">
          <h2>User Registration</h2>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleRegister}>Register</button>
        </div>
      )}

      {step === 2 && (
        <div className="room-container">
          <h2>Room Creation / Join</h2>
          <input
            type="text"
            placeholder="Enter room name"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
            disabled={!!createdRoomID}
          />
          <button onClick={handleCreateRoom} disabled={!!createdRoomID}>
            Create Room
          </button>
          <input
            type="text"
            placeholder="Enter room ID to join"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleJoinRoom}>Join Room</button>
          {createdRoomID && (
            <div>
              <p>Room Created: {roomName}</p>
              <p>Room ID: {createdRoomID}</p>
              <button onClick={() => navigate(`/chat/${createdRoomID}`)}>
                Go to Room
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const ChatRoom = () => {
  const { roomID } = useParams();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const username = localStorage.getItem("username") || "Anonymous";

  useEffect(() => {
    socket.on("receiveMessage", (newMessage) => {
      setMessages((prevMessages) => [...prevMessages, newMessage]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  const sendMessage = () => {
    if (message.trim()) {
      const newMessage = { sender: username, text: message, roomID };
      socket.emit("sendMessage", newMessage);
      setMessages((prevMessages) => [...prevMessages, newMessage]);
      setMessage("");
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div className="chat-room-container">
      <div className="chat-room-header">
        <h2>Room: {roomID}</h2>
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`chat-message ${msg.sender === username ? "self" : "other"}`}
          >
            <strong>{msg.sender}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyPress}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default App;
