// export default ChatApp;



import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

const socket = io("http://192.168.101.32:4000");

const ChatApp = () => {
  const [username, setUsername] = useState(""); 
  const [email, setEmail] = useState(""); 
  const [password, setPassword] = useState(""); 
  const [roomID, setRoomID] = useState(""); 
  const [message, setMessage] = useState(""); 
  const [messages, setMessages] = useState([]); 
  const [isInRoom, setIsInRoom] = useState(false); 
  const [createdRoomID, setCreatedRoomID] = useState(""); 
  const [isRegistered, setIsRegistered] = useState(false); 
  const [isJoiningRoom, setIsJoiningRoom] = useState(false); 
  const [isLoggingIn, setIsLoggingIn] = useState(false);
  const [userList, setUserList] = useState([]); 
  const [activeUser, setActiveUser] = useState(""); 

  useEffect(() => {
    socket.on("receiveMessage", (newMessage) => {
      if (newMessage.sender !== username) {
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      }
    });

    socket.on("message", (systemMessage) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "System", text: systemMessage },
      ]);
    });

    socket.on("roomCreated", ({ roomID, username }) => {
      setCreatedRoomID(roomID);
      setRoomID(roomID);
      setIsInRoom(true);
    });

    socket.on("roomJoined", ({ roomID, username }) => {
      setRoomID(roomID);
      setIsInRoom(true);
    });

    socket.on("errorMessage", (error) => {
      alert(error);
    });

    socket.on("userList", (users) => {
      setUserList(users);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("message");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("errorMessage");
      socket.off("userList");
    };
  }, [username]);

  const handleSignup = () => {
    if (username.trim() && email.trim() && password.trim()) {
      setIsRegistered(true);
    } else {
      alert("Please enter name, email, and password!");
    }
  };

  const handleLogin = () => {
    if (username.trim() && email.trim()) {
      setIsLoggingIn(true);
    } else {
      alert("Please enter your name and email!");
    }
  };

  const createRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Please enter your name.");
    }
  };

  const joinRoom = () => {
    if (username.trim() && roomID.trim()) {
      socket.emit("joinRoom", { roomID, username });
    } else {
      alert("Please enter your name and room ID.");
    }
  };

  const sendMessage = () => {
    if (message.trim() && roomID.trim()) {
      socket.emit("sendMessage", { roomID, username, message });
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "You", text: message },
      ]);
      setMessage("");
    }
  };

  const selectUser = (user) => {
    setActiveUser(user);
  };

  return (
    <div className="chat-container">
      {!isRegistered && !isLoggingIn ? (
        <div className="registration-container">
          <h2>Sign Up</h2>
          <input
            type="text"
            placeholder="Your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleSignup}>Submit</button>
        </div>
      ) : !isInRoom && isLoggingIn ? (
        <div className="login-container">
          <h2>Login</h2>
          <input
            type="text"
            placeholder="Your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleLogin}>Submit</button>
        </div>
      ) : !isInRoom ? (
        <div className="room-container">
          <h2>Enter Your Room</h2>
          <input
            type="text"
            placeholder="Enter room name"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
            disabled={isJoiningRoom}
          />
          <div className="room-buttons">
            <button onClick={createRoom} className="room-button">
              Create Room
            </button>
            <button
              onClick={() => setIsJoiningRoom(true)}
              disabled={createdRoomID}
              className="room-button"
            >
              Join Room
            </button>
          </div>

          {isJoiningRoom && (
            <div>
              <input
                type="text"
                placeholder="Room ID"
                value={roomID}
                onChange={(e) => setRoomID(e.target.value)}
              />
              <button onClick={joinRoom}>Join Room</button>
            </div>
          )}
        </div>
      ) : (
        <>
          <div className="chat-header">
            <h2>Room: {roomID}</h2>
            <p>Logged in as: {username}</p>
          </div>

          <div className="chat-users">
            <h3>Users</h3>
            {userList.map((user, index) => (
              <div
                key={index}
                className="user-item"
                onClick={() => selectUser(user)}
              >
                {user}
              </div>
            ))}
          </div>

          <div className="chat-window">
            {activeUser && (
              <>
                <div className="chat-header">
                  <h2>Chat with: {activeUser}</h2>
                </div>

                <div className="chat-messages">
                  {messages.map((msg, index) => (
                    <div
                      key={index}
                      className={msg.sender === "You" ? "message-sender" : "message-receiver"}
                    >
                      <strong>{msg.sender}:</strong> {msg.text}
                    </div>
                  ))}
                </div>

                <div className="chat-input">
                  <input
                    type="text"
                    placeholder="Type your message..."
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                  />
                  <button onClick={sendMessage}>Send</button>
                </div>
              </>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default ChatApp;
