// export default ChatApp;



import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

const socket = io("http://192.168.101.32:4000");

const ChatApp = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [roomID, setRoomID] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [view, setView] = useState("home"); // Tracks current view: home, signup, login, room, chat
  const [users, setUsers] = useState([]); // Users in the room

  useEffect(() => {
    // Socket listeners
    socket.on("receiveMessage", (newMessage) => {
      setMessages((prev) => [...prev, newMessage]);
    });

    socket.on("message", (systemMessage) => {
      setMessages((prev) => [
        ...prev,
        { sender: "System", text: systemMessage },
      ]);
    });

    socket.on("roomCreated", ({ roomID }) => {
      setRoomID(roomID);
      setView("chat");
    });

    socket.on("roomJoined", ({ roomID, users }) => {
      setRoomID(roomID);
      setUsers(users);
      setView("chat");
    });

    socket.on("updateUsers", (userList) => {
      setUsers(userList);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("message");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("updateUsers");
    };
  }, []);

  const handleSignup = () => {
    if (username && email && password) {
      alert("Signed up successfully!");
      setView("room");
    } else {
      alert("Please fill in all fields!");
    }
  };

  const handleLogin = () => {
    if (username && email) {
      alert("Logged in successfully!");
      setView("room");
    } else {
      alert("Please fill in all fields!");
    }
  };

  const createRoom = () => {
    if (username.trim()) {
      socket.emit("createRoom", username);
    } else {
      alert("Enter your name before creating a room.");
    }
  };

  const joinRoom = () => {
    if (roomID.trim() && username.trim()) {
      socket.emit("joinRoom", { roomID, username });
    } else {
      alert("Enter your name and room ID before joining.");
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("sendMessage", { roomID, username, message });
      setMessages((prev) => [...prev, { sender: "You", text: message }]);
      setMessage("");
    }
  };

  return (
    <div className="chat-container">
      {view === "home" && (
        <div className="home-container">
          <button onClick={() => setView("signup")}>Sign Up</button>
          <button onClick={() => setView("login")}>Login</button>
        </div>
      )}

      {view === "signup" && (
        <div className="form-container">
          <h2>Sign Up</h2>
          <input
            type="text"
            placeholder="Enter your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleSignup}>Submit</button>
        </div>
      )}

      {view === "login" && (
        <div className="form-container">
          <h2>Login</h2>
          <input
            type="text"
            placeholder="Enter your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleLogin}>Submit</button>
        </div>
      )}

      {view === "room" && (
        <div className="room-container">
          <h2>Enter Room</h2>
          <input
            type="text"
            placeholder="Enter room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <div className="room-buttons">
            <button onClick={createRoom}>Create Room</button>
            <button onClick={joinRoom}>Join Room</button>
          </div>
        </div>
      )}

      {view === "chat" && (
        <>
          <div className="chat-header">
            <h2>Room: {roomID}</h2>
            <p>Logged in as: {username}</p>
          </div>
          <div className="chat-body">
            <div className="user-list">
              <h4>Users</h4>
              {users.map((user, index) => (
                <div key={index} className="user-item">
                  {user}
                </div>
              ))}
            </div>
            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div
                  key={index}
                  className={
                    msg.sender === "You"
                      ? "message-sender"
                      : msg.sender === "System"
                      ? "message-system"
                      : "message-receiver"
                  }
                >
                  <strong>{msg.sender}:</strong> {msg.text}
                </div>
              ))}
            </div>
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatApp;
