import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

const socket = io("http://localhost:4000");

const ChatApp = () => {
  const [username, setUsername] = useState(""); // Current user's name
  const [users, setUsers] = useState({}); // Active users list
  const [receiver, setReceiver] = useState(""); // Selected user to chat with
  const [message, setMessage] = useState(""); // Message to send
  const [messages, setMessages] = useState([]); // Chat history

  // Register the user
  useEffect(() => {
    const name = prompt("Enter your username:");
    setUsername(name);
    socket.emit("register", name);
  }, []);

  // Load users and messages
  useEffect(() => {
    socket.on("users", (activeUsers) => setUsers(activeUsers));
    socket.on("messageReceived", (newMessage) => {
      setMessages((prev) => [...prev, newMessage]);
    });
    socket.on("messageSent", (sentMessage) => {
      setMessages((prev) => [...prev, sentMessage]);
    });
  }, []);

  const sendMessage = () => {
    if (message.trim() && receiver) {
      socket.emit("privateMessage", {
        sender: username,
        receiver,
        text: message,
      });
      setMessage(""); // Clear input field
    } else {
      alert("Select a user to send the message!");
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Chat Application</h2>
        <p>Logged in as: {username}</p>
      </div>

      <div className="chat-users">
        <h3>Active Users</h3>
        <ul>
          {Object.entries(users).map(([user, socketId]) => (
            <li
              key={socketId}
              onClick={() => setReceiver(user)}
              style={{
                fontWeight: receiver === user ? "bold" : "normal",
                cursor: "pointer",
              }}
            >
              {user} {user === username ? "(You)" : ""}
            </li>
          ))}
        </ul>
      </div>

      <div className="chat-messages">
        <h3>Messages</h3>
        {messages
          .filter(
            (msg) =>
              (msg.sender === username && msg.receiver === receiver) ||
              (msg.sender === receiver && msg.receiver === username)
          )
          .map((msg, index) => (
            <div
              key={index}
              className={
                msg.sender === username ? "message-sender" : "message-receiver"
              }
            >
              <p>
                {msg.text}{" "}
                <span>{new Date(msg.timestamp).toLocaleTimeString()}</span>
              </p>
            </div>
          ))}
      </div>

      <div className="chat-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatApp;
