import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

const socket = io("http://localhost:4000");

const ChatApp = () => {
  const [roomID, setRoomID] = useState(""); // Room ID input
  const [message, setMessage] = useState(""); // Message input
  const [messages, setMessages] = useState([]); // Chat history
  const [isInRoom, setIsInRoom] = useState(false); // Track if user is in a room

  // Listen for incoming messages
  useEffect(() => {
    socket.on("receiveMessage", (newMessage) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "Other", text: newMessage },
      ]);
    });

    socket.on("message", (systemMessage) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "System", text: systemMessage },
      ]);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("message");
    };
  }, []);

  // Join a room
  const joinRoom = () => {
    if (roomID.trim()) {
      socket.emit("joinRoom", roomID);
      setIsInRoom(true); // Mark user as in a room
    }
  };

  // Send message to the server
  const sendMessage = () => {
    if (message.trim() && roomID.trim()) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "You", text: message },
      ]);
      socket.emit("sendMessage", { roomID, message }); // Emit message with room ID
      setMessage(""); // Clear input field
    }
  };

  return (
    <div className="chat-container">
      {!isInRoom ? (
        <div className="room-container">
          <h2>Enter Room ID</h2>
          <input
            type="text"
            placeholder="Room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={joinRoom}>Join Room</button>
        </div>
      ) : (
        <>
          <div className="chat-header">
            <h2>Room: {roomID}</h2>
          </div>

          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={
                  msg.sender === "You"
                    ? "message-sender"
                    : msg.sender === "System"
                    ? "message-system"
                    : "message-receiver"
                }
              >
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatApp;
