      roomID,
      username
    }) => {
      console.log(`${username} joined room: ${roomID}`);
      setRoomID(roomID);
      setIsInRoom(true);
    });

    // Event: Error messages
    socket.on("errorMessage", error => {
      alert(error);
    });

    // Cleanup listeners on component unmount
    return () => {
      socket.off("receiveMessage");
      socket.off("message");
      socket.off("roomCreated");
      socket.off("roomJoined");
