import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./ChatApp.css";

// Initialize Socket.IO client
const socket = io("http://192.168.101.32:4000");

const ChatApp = () => {
  const [username, setUsername] = useState(""); // User's name
  const [email, setEmail] = useState(""); // User's email
  const [roomID, setRoomID] = useState(""); // Room ID
  const [message, setMessage] = useState(""); // Message input
  const [messages, setMessages] = useState([]); // Chat history
  const [isInRoom, setIsInRoom] = useState(false); // Track if user is in a room
  const [createdRoomID, setCreatedRoomID] = useState(""); // Store generated room ID
  const [isRegistered, setIsRegistered] = useState(false); // Registration status
  const [isJoiningRoom, setIsJoiningRoom] = useState(false); // Track if user is joining a room

  // Listen for incoming events from the backend
  useEffect(() => {
    // Event: New message received
    socket.on("receiveMessage", (newMessage) => {
      if (newMessage.sender !== username) {
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      }
    });

    // Event: System messages
    socket.on("message", (systemMessage) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "System", text: systemMessage },
      ]);
    });

    // Event: Room created
    socket.on("roomCreated", ({ roomID, username }) => {
      console.log("Room created successfully:", { roomID, username });
      setCreatedRoomID(roomID);
      setRoomID(roomID);
      setIsInRoom(true);
    });

    // Event: Room joined
    socket.on("roomJoined", ({ roomID, username }) => {
      console.log(`${username} joined room: ${roomID}`);
      setRoomID(roomID);
      setIsInRoom(true);
    });

    // Event: Error messages
    socket.on("errorMessage", (error) => {
      alert(error);
    });

    // Cleanup listeners on component unmount
    return () => {
      socket.off("receiveMessage");
      socket.off("message");
      socket.off("roomCreated");
      socket.off("roomJoined");
      socket.off("errorMessage");
    };
  }, [username]);

  // Function: Create a new room
  const createRoom = () => {
    if (username.trim()) {
      console.log("Requesting room creation for:", username);
      socket.emit("createRoom", username);
    } else {
      alert("Please enter your name first.");
    }
  };

  // Function: Join an existing room
  const joinRoom = () => {
    if (username.trim() && roomID.trim()) {
      console.log(`Requesting to join room ${roomID} as ${username}`);
      socket.emit("joinRoom", { roomID, username });
    } else {
      alert("Please enter your name and room ID.");
    }
  };

  // Function: Send a message
  const sendMessage = () => {
    if (message.trim() && roomID.trim()) {
      // Add the message to the sender's local chat history
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "You", text: message },
      ]);

      // Emit the message to the server
      socket.emit("sendMessage", { roomID, username, message });

      // Clear the input field
      setMessage("");
    }
  };

  // Handle user registration (Name + Email)
  const handleRegistration = () => {
    if (username.trim() && email.trim()) {
      setIsRegistered(true);
    } else {
      alert("Please enter both name and email!");
    }
  };

  return (
    <div className="chat-container">
      {!isRegistered ? (
        // Registration Step
        <div className="registration-container">
          <h2>Enter Your Details</h2>
          <input
            type="text"
            placeholder="Your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleRegistration}>Submit</button>
        </div>
      ) : !isInRoom ? (
        // Room creation / joining Step
        <div className="room-container">
          <h2>Enter Your Room</h2>
          <input
            type="text"
            placeholder="Enter room name"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
            disabled={isJoiningRoom} // Disable room ID input if joining room
          />
          <div className="room-buttons">
            <button
              onClick={createRoom}
              disabled={isJoiningRoom}
              className="room-button"
            >
              Create Room
            </button>
            <button
              onClick={() => setIsJoiningRoom(true)}
              disabled={createdRoomID}
              className="room-button"
            >
              Join Room
            </button>
          </div>

          {/* If user is joining, show the join button */}
          {isJoiningRoom && (
            <div>
              <input
                type="text"
                placeholder="Room ID"
                value={roomID}
                onChange={(e) => setRoomID(e.target.value)}
              />
              <button onClick={joinRoom}>Join Room</button>
            </div>
          )}
        </div>
      ) : (
        // Chat Room Step
        <>
          <div className="chat-header">
            <h2>Room: {roomID}</h2>
            <p>Logged in as: {username}</p>
          </div>

          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={
                  msg.sender === "You"
                    ? "message-sender"
                    : msg.sender === "System"
                    ? "message-system"
                    : "message-receiver"
                }
              >
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatApp;
