import React, { useState } from "react";
import "./ChatApp.css";

const ChatApp = () => {
  const [message, setMessage] = useState(""); // Message input ka state
  const [messages, setMessages] = useState([]); // Messages list ka state

  // Message send karne ka function
  const sendMessage = () => {
    if (message.trim()) {
      // Add sender's message to the list
      setMessages((prevMessages) => [...prevMessages, { text: message, sender: true }]);
      setMessage(""); // Clear input field
    }
  };

  // Simulate receiving a message
  const receiveMessage = () => {
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: "This is a message from the receiver!", sender: false },
    ]);
  };

  return (
    <div className="chat-container">
      {/* Chat Header */}
      <div className="chat-header">
        <h2>Chat with John Doe</h2>
      </div>

      {/* Messages Display */}
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.sender ? "message-sender" : "message-receiver"}`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      {/* Input and Send Button */}
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
        <button onClick={receiveMessage} style={{ marginLeft: "10px" }}>
          Simulate Receive
        </button>
      </div>
    </div>
  );
};

export default ChatApp;
