import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./RoomCreationPage.css";

const RoomCreationPage = () => {
  const [roomName, setRoomName] = useState("");
  const [roomID, setRoomID] = useState(""); // This will be used for joining a room
  const [createdRoom, setCreatedRoom] = useState(null);
  const [isJoinRoom, setIsJoinRoom] = useState(false); // Track if user is joining a room
  const navigate = useNavigate();

  // Simulate room creation (Replace with actual backend API)
  const handleCreateRoom = () => {
    if (roomName.trim()) {
      // Simulate creating a room with an ID (backend call would return this)
      const generatedRoomID = "ROOM" + Math.floor(Math.random() * 1000);
      setCreatedRoom({ name: roomName, id: generatedRoomID });

      // Simulate backend room creation logic
      // Backend code would look like: socket.emit("createRoom", roomName)
      alert(`Room "${roomName}" created with ID: ${generatedRoomID}`);
    } else {
      alert("Please enter a room name!");
    }
  };

  // Simulate joining a room (Replace with actual backend API)
  const handleJoinRoom = () => {
    if (roomID.trim()) {
      // Simulate backend joining logic
      alert(`Joining room: ${roomID}`);
      navigate(`/chat/${roomID}`); // Navigate to the chat room
    } else {
      alert("Please enter a valid room ID!");
    }
  };

  return (
    <div className="room-container">
      <h2>Room Creation / Join</h2>

      {/* Room Name Input */}
      <input
        type="text"
        placeholder="Enter your room name"
        value={roomName}
        onChange={(e) => setRoomName(e.target.value)}
        disabled={isJoinRoom || createdRoom} // Disable when joining or room is created
      />

      {/* Horizontal Buttons */}
      <div className="room-buttons">
        <button
          onClick={handleCreateRoom}
          disabled={isJoinRoom || createdRoom}
          className="room-button"
        >
          Create Room
        </button>
        <button
          onClick={() => setIsJoinRoom(true)}
          disabled={createdRoom}
          className="room-button"
        >
          Join Room
        </button>
      </div>

      {/* Show room ID input if user chooses to join a room */}
      {isJoinRoom && (
        <>
          <input
            type="text"
            placeholder="Enter room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
            disabled={createdRoom} // Disable if room is created
          />
          <button onClick={handleJoinRoom} className="join-button">
            Join Room
          </button>
        </>
      )}

      {/* Show created room details */}
      {createdRoom && (
        <div className="created-room">
          <h3>Room Created: {createdRoom.name}</h3>
          <p>Room ID: {createdRoom.id}</p>
          <button
            onClick={() => navigate(`/chat/${createdRoom.id}`)}
            className="go-to-room-button"
          >
            Go to Room
          </button>
        </div>
      )}
    </div>
  );
};

export default RoomCreationPage;
